import React, { useEffect, useState, useCallback, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { Form } from '@unform/web';
import { FormHandles } from '@unform/core';
import {
  FiCalendar,
  FiDollarSign,
  FiFileText,
  FiPercent,
} from 'react-icons/fi';
import { ValidationError as YupValidationError } from 'yup';
import { toast } from 'react-toastify';
import { isPast, differenceInCalendarMonths, parseISO } from 'date-fns';

import { Container, Main, InputGroup, Debit, FormGroup } from './styles';
import { paymentMethods } from '../../utils/defaults';
import Loading from '../../components/Loading';
import Title from '../../components/Title';
import Table from '../../components/Table';
import Header from '../../components/Header';
import Aside from '../../components/Aside';
import Input from '../../components/Input';
import Select from '../../components/SelectInput';
import Button from '../../components/Button';
import Popup from '../../components/Popup';
import Document from '../../components/Document';
import IDebit from '../../entities/IDebit';
import IPayment from '../../entities/IPayment';
import api from '../../services/api';
import paymentSchema from '../../schemas/paymentSchema';
import getValidationErrors from '../../utils/getValidationErrors';
import { prettyDate, formatCoin } from '../../utils/formatFunctions';

interface IFormData {
  method: 'creditCard' | 'debitCard' | 'cash' | 'check' | 'deposit' | 'slip';
}

interface IParams {
  contract_id: string;
}

interface IDebitWithVariation extends IDebit {
  true_value?: number;
}

const ExtraDebitsByContract: React.FC = () => {
  const formRef = useRef<FormHandles>(null);

  const { contract_id } = useParams<IParams>();

  const [debits, setDebits] = useState([] as IDebitWithVariation[]);
  const [selectedDebit, setSelectedDebit] = useState({} as IDebitWithVariation);
  const [payment, setPayment] = useState({} as IPayment);
  const [loadingPage, setLoadingPage] = useState(false);
  const [loadingSubmit, setLoadingSubmit] = useState(false);

  const handleEditExtraDebit = useCallback(() => {}, []);

  useEffect(() => {
    setLoadingPage(true);
    api
      .get(`contracts/${contract_id}/debits/extra`)
      .then(response => {
        const debitsFromApi = response.data;

        debitsFromApi.forEach((debit: IDebitWithVariation) => {
          const parsedDebitDate = parseISO(debit.payment_limit_date.toString());

          let true_value = debit.value;

          if (isPast(parsedDebitDate)) {
            const months = differenceInCalendarMonths(
              new Date(),
              parsedDebitDate,
            );

            true_value = debit.value * 1.03 ** months;
          } else {
            true_value = debit.value - (debit.value * debit.discount) / 100;
          }

          Object.assign(debit, { true_value });
        });

        setDebits(response.data);
      })
      .catch(() => {
        toast.error(
          'Erro ao carregar dÃ©bitos! Por favor, tente novamente mais tarde.',
        );
      })
      .finally(() => {
        setLoadingPage(false);
      });
  }, [contract_id]);

  return (
    <Container>
      <Loading show={loadingPage} />

      <Header />

      <Aside />

      <Main>
        <Title
          title="Gerenciar dÃ©bitos adicionais"
          subtitle={`Contrato #${contract_id}`}
        />

        <Form ref={formRef} onSubmit={handleEditExtraDebit}>
          <FormGroup>
            <InputGroup>
              <Input
                icon={FiFileText}
                name="description"
                placeholder="DescriÃ§Ã£o"
              />

              <Input
                icon={FiDollarSign}
                type="number"
                step="0.01"
                name="value"
                placeholder="Valor"
              />
            </InputGroup>

            <InputGroup>
              <Input
                icon={FiPercent}
                type="number"
                step="0.01"
                name="discount"
                placeholder="Desconto"
              />

              <Input
                icon={FiCalendar}
                type="date"
                name="payment_limit_date"
                label="Data limite de pagamento"
              />
            </InputGroup>

            <Button type="submit">Editar</Button>
          </FormGroup>
        </Form>

        <Table
          isVoid={debits.length <= 0}
          voidMessage="NÃ£o hÃ¡ dÃ©bitos para serem pagos!"
        >
          <thead>
            <tr>
              <td>DescriÃ§Ã£o</td>
              <td>Valor</td>
              <td>Valor com variaÃ§Ã£o</td>
              <td>Data limite do pagamento</td>
              <td>Data de pagamento</td>
              <td>AÃ§Ãµes</td>
            </tr>
          </thead>
          <tbody>
            {debits.map(debit => (
              <Debit
                paid={debit.paid}
                key={debit.id}
                onClick={() => setSelectedDebit(debit)}
              >
                <td>{debit.description}</td>
                <td>{formatCoin(debit.value)}</td>
                <td>
                  {debit.true_value
                    ? formatCoin(debit.true_value)
                    : debit.value}
                </td>
                <td>{prettyDate(debit.payment_limit_date)}</td>
                <td>{debit.payday ? prettyDate(debit.payday) : '-'}</td>
                <td>BotÃµes</td>
              </Debit>
            ))}
          </tbody>
        </Table>
      </Main>
    </Container>
  );
};

export default ExtraDebitsByContract;
