import styled, { css } from 'styled-components';

import { BaseGridTemplate, BaseGridMain } from '../../styles/grid';

interface IContractProps {
  selected?: boolean;
}

export const Container = styled(BaseGridTemplate)``;

export const Main = styled(BaseGridMain)``;

export const ContractsList = styled.div``;

export const Contract = styled.div<IContractProps>`
  ${props =>
    props.selected &&
    css`
      border: 1px solid #013c64;
    `}
`;

export const FormGroup = styled.div`
  display: flex;
  flex-direction: column;

  button {
    align-self: flex-end;
    margin-top: 12px;
  }
`;

export const InputGroup = styled.div`
  display: flex;
  align-items: flex-end;

  > div {
    & + div {
      margin-left: 12px;
    }
  }

  & + div {
    margin-top: 12px;
  }
`;
