import React, { useRef, useCallback, useState, useEffect } from 'react';
import { FormHandles } from '@unform/core';
import { Form } from '@unform/web';
import { useParams } from 'react-router-dom';
import { toast } from 'react-toastify';

import {
  Container,
  Main,
  ContractsList,
  Contract,
  FormGroup,
  InputGroup,
} from './styles';
import Header from '../../components/Header';
import Aside from '../../components/Aside';
import Title from '../../components/Title';
import Input from '../../components/Input';
import Button from '../../components/Button';
import Loading from '../../components/Loading';
import IContract from '../../entities/IContract';
import api from '../../services/api';

interface IParams {
  grade_id: string;
}

const CreateExtraDebitMenu: React.FC = () => {
  const [contracts, setContracts] = useState<IContract[]>([]);
  const [loadingPage, setLoadingPage] = useState(true);
  const [selectedContracts, setSelectedContract] = useState<string[]>([]);

  const formRef = useRef<FormHandles>(null);

  const { grade_id } = useParams<IParams>();

  const handleSubmitForm = useCallback(() => {}, []);

  const handleSelectContract = useCallback((contract_id: string) => {}, []);

  useEffect(() => {
    api
      .get(`/contracts/active/grades/${grade_id}`)
      .then(response => {
        const contractsFromApi = response.data as IContract[];

        console.log(contractsFromApi);

        setContracts(contractsFromApi);
      })
      .catch(() => {
        toast.error(
          'Erro ao buscar dados do servidor! Por favor, tente novamente mais tarde.',
        );
      })
      .finally(() => {
        setLoadingPage(false);
      });
  }, [grade_id]);

  return (
    <Container>
      <Loading show={loadingPage} />

      <Header />

      <Aside />

      <Main>
        <Title
          title="Criar débitos adicionais"
          subtitle="Preencha os dados do débito e selecione as matrículas em que serão aplicados"
        />

        <Form ref={formRef} onSubmit={handleSubmitForm}>
          <FormGroup>
            <InputGroup>
              <Input name="description" placeholder="Descrição" />

              <Input
                type="number"
                step="0.01"
                name="value"
                placeholder="Valor"
              />
            </InputGroup>

            <InputGroup>
              <Input
                type="number"
                step="0.01"
                name="discount"
                placeholder="Desconto"
              />

              <Input
                type="date"
                name="payment_limit_date"
                label="Data limite de pagamento"
              />
            </InputGroup>

            <Button type="submit">Criar</Button>
          </FormGroup>

          <ContractsList>
            {contracts.map(contract => (
              <Contract
                key={contract.id}
                onclick={() => handleSelectContract(contract.id)}
              >
                <span>{contract.student.name}</span>
              </Contract>
            ))}
          </ContractsList>
        </Form>
      </Main>
    </Container>
  );
};

export default CreateExtraDebitMenu;
