import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { Form } from '@unform/web';
import { FiCheck } from 'react-icons/fi';
import { toast } from 'react-toastify';

import { Container, Main, InputGroup, WarnMessage, Payment } from './styles';
import Loading from '../../components/Loading';
import Table from '../../components/Table';
import Header from '../../components/Header';
import Aside from '../../components/Aside';
import Title from '../../components/Title';
import Input from '../../components/Input';
import Button from '../../components/Button';
import Popup from '../../components/Popup';
import Document from '../../components/Document';
import IPayment from '../../entities/IPayment';
import IDischarge from '../../entities/IDischarge';
import api from '../../services/api';
import { formatPaymentMethod, formatCoin } from '../../utils/formatFunctions';
import IContract from '../../entities/IContract';

interface IParams {
  contract_id: string;
}

const GenerateDocumentsByContract: React.FC = () => {
  const { contract_id } = useParams<IParams>();

  const [payments, setPayments] = useState([] as IPayment[]);
  const [confirmMessage, setConfirmMessage] = useState('');
  const [contract, setContract] = useState({} as IContract);
  const [discharge, setDischarge] = useState({} as IDischarge);
  const [loadingPage, setLoadingPage] = useState(false);
  const [loadingSubmit, setLoadingSubmit] = useState(false);

  useEffect(() => {
    setLoadingPage(true);
    api
      .get(`contracts/${contract_id}`)
      .then(response => setContract(response.data))
      .catch(() => {
        toast.error(
          'Erro ao carragar contrato! Por favor, tente novamente mais tarde.',
        );
      })
      .finally(() => {
        setLoadingPage(false);
      });
  }, [contract_id]);

  return (
    <Container>
      <Loading show={loadingPage} />

      <Header />

      <Aside />

      <Main>
        <Title title="Gerar documentos" />
      </Main>
    </Container>
  );
};

export default GenerateDocumentsByContract;
